package com.roc.common.utils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.ApplicationContext;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;@Componentpublic class AutoWiredToStatic {    private static AutoWiredToStatic instance;     @Autowired    private ApplicationContext applicationContext;     @PostConstruct    public void registerInstance() {        instance = this;    }     public static <T> T getBean(Class<T> clazz) {        return instance.applicationContext.getBean(clazz);    }}