package com.roc.system.domain;import java.util.concurrent.atomic.AtomicLong;public class SysSequenceRange {    /**     * 最小值     */    private final long min;    /**     * 最大值     */    private final long max;    /**     * 当前值     */    private final AtomicLong value;    /**     * 是否超限     */    private volatile boolean over = false;    public SysSequenceRange(long min, long max) {        this.min = min;        this.max = max;        this.value = new AtomicLong(min);    }    public long getAndIncrement() {        long currentValue = value.getAndIncrement();        if (min == -1 || max == -1 || currentValue > max) {            over = true;            return -1;        }        return currentValue;    }}