package com.roc.system.mapper;import com.roc.system.domain.SysSequenceDTO;import org.apache.ibatis.annotations.Mapper;import java.util.List;@Mapperpublic interface SysSequenceMapper {    /**     * 功能描述: 获取指定的序列行，并进行for update的行锁操作     */    SysSequenceDTO getSequenceByIdForUpdate(SysSequenceDTO sequenceDTO);    /**     * 功能描述: 更新指定的Sequence值     */    int updateSequenceById(SysSequenceDTO sequenceDTO);    /**     * 功能描述: 获取全部序列行     */    List<SysSequenceDTO> getAllSequence();    /**     * 功能描述: 从mysql获取uuid     */    String getUuidFromMysql();}